import { Route, Routes } from 'react-router-dom';
import { AppRoute, LIST_OF_GUITAR } from '../../const';
import { useAppDispatch, useAppSelector } from '../../hooks/hooks';
import GuitarPage from '../guitar-page';
import MainCard from '../main-card';
import NotFoundPage from '../notfound-page';
import LoadingScreen from '../loading-screen';
import { fetchGuitarsPriceAction } from '../../store/api-actions';

function App(): JSX.Element {
  const dispatch = useAppDispatch();

  const { guitars, isDataLoaded, guitarsPrice, isSorting} = useAppSelector(({ DATA }) => DATA);
  let someGuitars = guitars;

  if (isSorting === 'price') {
    console('тут')
    dispatch(fetchGuitarsPriceAction());
    someGuitars = guitarsPrice;
  }

  if (!isDataLoaded) {
    return (
      <LoadingScreen />
    );
  }

  return (
    <Routes>
      <Route
        path={AppRoute.Page1 && `${AppRoute.Page1}${AppRoute.SortPrice}`}
        element={<MainCard guitars={someGuitars.slice(LIST_OF_GUITAR[0].rangeFrom, LIST_OF_GUITAR[0].rangeTo)} />}
      />
      <Route
        path={AppRoute.Page2}
        element={<MainCard guitars={someGuitars.slice(LIST_OF_GUITAR[1].rangeFrom, LIST_OF_GUITAR[1].rangeTo)} />}
      />
      <Route
        path={AppRoute.Page3}
        element={<MainCard guitars={someGuitars.slice(LIST_OF_GUITAR[2].rangeFrom, LIST_OF_GUITAR[2].rangeTo)} />}
      />
      <Route
        path={AppRoute.Page1 }
        element={<MainCard guitars={someGuitars.slice(LIST_OF_GUITAR[0].rangeFrom, LIST_OF_GUITAR[0].rangeTo)} />}
      />
      <Route
        path={AppRoute.Page1 }
        element={<MainCard guitars={someGuitars.slice(LIST_OF_GUITAR[0].rangeFrom, LIST_OF_GUITAR[0].rangeTo)} />}
      />
      <Route
        path={AppRoute.Details}
        element={<GuitarPage tab />}
      />
      <Route
        path={AppRoute.Description}
        element={<GuitarPage tab={false} />}
      />
      <Route
        path="/*"
        element={<NotFoundPage />}
      />
    </Routes>
  );
}

export default App;
